// Parse CSV and get next unpublished article
const csv = $input.first().json.data || $input.first().json;
const lines = typeof csv === 'string' ? csv.split('\n') : [];
const headers = lines[0]?.split(',') || [];

// Find first article with status 'pending'
for (let i = 1; i < lines.length; i++) {
  const values = lines[i].split(',');
  const article = {};
  headers.forEach((h, idx) => article[h.trim()] = values[idx]?.trim());

  if (article.status === 'pending') {
    return [{ json: {
      rowIndex: i + 1,
      title: article.title,
      slug: article.slug,
      category: article.category,
      primaryKeyword: article.primary_keyword,
      secondaryKeywords: article.secondary_keywords?.split(';') || [],
      targetCity: article.target_city || 'Atlanta',
      wordCount: parseInt(article.word_count) || 1500,
      readTime: parseInt(article.read_time) || 7,
      postToSocial: article.post_to_social === 'yes'
    }}];
  }
}

throw new Error('No pending articles found in calendar');

