try {
  // Try to parse JSON from response
  let research = content;
  if (typeof content === 'string') {
    // Remove markdown code blocks if present
    research = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    research = JSON.parse(research);
  }

  const articleData = $('üìã Prepare Data').first().json;

  return [{
    json: {
      ...articleData,
      research: {
        faq_questions: research.faq_questions || [],
        unique_angles: research.unique_angles || [],
        atlanta_data: research.atlanta_data || [],
        competitor_gaps: research.competitor_gaps || [],
        pricing_data: research.pricing_data || {}
      }
    }
  }];
} catch (e) {
  // If JSON parse fails, continue with default research
  const articleData = $('üìã Prepare Data').first().json;
  return [{
    json: {
      ...articleData,
      research: {
        faq_questions: ['What does professional cleaning cost?', 'How often should I clean?', 'Is deep cleaning worth it?'],
