const response = $input.first().json;
let articleContent = response.choices?.[0]?.message?.content || '';
const prevData = $('üîÑ Parse Outline').first().json;

// Clean up the content
articleContent = articleContent
  .replace(/```html\n?/g, '')
  .replace(/```\n?/g, '')
  .trim();

// Count words
const wordCount = articleContent.replace(/<[^>]*>/g, ' ').split(/\s+/).filter(w => w.length > 0).length;

return [{
  json: {
    ...prevData,
    articleContent: articleContent,
    wordCount: wordCount
  }
}];

