const response = $input.first().json;
const content = response.choices?.[0]?.message?.content || '{}';
const prevData = $('üîÑ Parse SEO').first().json;

try {
  let review = content;
  if (typeof content === 'string') {
    review = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    review = JSON.parse(review);
  }

  return [{
    json: {
      ...prevData,
      quality: {
        scores: review.scores || {},
        totalScore: review.total_score || 0,
        passed: review.passed || false,
        issues: review.issues || [],
        improvements: review.improvements || []
      }
    }
  }];
} catch (e) {
  // If can't parse, auto-pass if word count is good
  const passed = prevData.wordCount >= 1500;
  return [{
    json: {
      ...prevData,
      quality: {
        scores: { word_count: passed ? 100 : 50 },
        totalScore: passed ? 85 : 60,
        passed: passed,
        issues: passed ? [] : ['Could not verify quality'],
        improvements: []
      }
    }
  }];
}

